import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import MedicinesList from './pages/MedicinesList';
import SalesList from './pages/SalesList';
import InventoryList from './pages/InventoryList';
import AdminDashboard from './pages/AdminDashboard';
import PharmacistDashboard from './pages/PharmacistDashboard';
import InvoiceSearch from './pages/InvoiceSearch';
import AddMedicine from './pages/AddMedicine';
import AuthPage from './components/AuthPage';
import AdminNavigation from './components/AdminNavigation';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    const savedRole = localStorage.getItem('userRole');
    if (token && savedUser) {
      setUser(JSON.parse(savedUser));
      setUserRole(savedRole || null);
    }
    setLoading(false);
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    setUserRole(userData.role);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('userRole');
    setUser(null);
    setUserRole(null);
  };

  // Navigation links based on role
  const adminLinks = [
    { path: '/admin-dashboard', label: 'Dashboard' },
    { path: '/invoice-search', label: 'Invoice Search' },
    { path: '/inventory', label: 'Medicine Inventory' },
    { 
      label: 'Pharmacy Company', 
      dropdown: [
        { path: '/company/add', label: 'Add Company' },
        { path: '/company/manage', label: 'Manage Company' }
      ]
    },
    { 
      label: 'Medicine', 
      dropdown: [
        { path: '/medicines/add', label: 'Add Medicine' },
        { path: '/medicines', label: 'Manage Medicine' }
      ]
    },
    { 
      label: 'Pharmacist', 
      dropdown: [
        { path: '/pharmacist/add', label: 'Add Pharmacist' },
        { path: '/pharmacist/manage', label: 'Manage Pharmacist' }
      ]
    },
    { 
      label: 'Reports', 
      dropdown: [
        { path: '/reports/stock', label: 'Stock Reports' },
        { path: '/reports/pharmacist', label: 'Pharmacist Reports' },
        { path: '/reports/sales', label: 'Sales Reports' }
      ]
    },
    { path: '/invoice', label: 'Invoice' }
  ];

  const pharmacistLinks = [
    { path: '/pharmacist-dashboard', label: 'Dashboard' },
    { path: '/medicines', label: 'Medicines' },
    { path: '/inventory', label: 'Inventory' },
    { path: '/sales', label: 'Sales' },
  ];

  const navLinks = userRole === 'admin' ? adminLinks : pharmacistLinks;

  if (loading) return <div>Loading...</div>;

  if (!user) {
    return <AuthPage onLogin={handleLogin} />;
  }

  return (
    <Router>
      <div className="App">
        <nav className="navbar">
          <h1>Pharmacy System</h1>
          {userRole === 'admin' ? (
            <AdminNavigation navLinks={adminLinks} />
          ) : (
            <ul>
              {navLinks.map((link) => (
                <li key={link.path}>
                  <Link to={link.path}>{link.label}</Link>
                </li>
              ))}
            </ul>
          )}
          <div className="user-info">
            <span className={`role-badge ${userRole}`}>
              {userRole === 'admin' ? 'üë®üíº' : 'üë®‚öïÔ∏è'} {userRole}
            </span>
            <span className="user-name">Welcome, {user.username}</span>
            <button onClick={handleLogout} className="btn btn-secondary">Logout</button>
          </div>
        </nav>

        <main className="main-content">
          <Routes>
            {/* Role-based dashboard redirects */}
            <Route 
              path="/" 
              element={
                userRole === 'admin' 
                  ? <Navigate to="/admin-dashboard" replace /> 
                  : <Navigate to="/pharmacist-dashboard" replace />
              } 
            />

            {/* Admin-only routes */}
            {userRole === 'admin' && (
              <>
                <Route path="/admin-dashboard" element={<AdminDashboard />} />
                <Route path="/invoice-search" element={<InvoiceSearch />} />
                <Route path="/company/add" element={<div className="page-placeholder"><h2>Add Company</h2><p>Add new pharmacy company</p></div>} />
                <Route path="/company/manage" element={<div className="page-placeholder"><h2>Manage Company</h2><p>Manage existing companies</p></div>} />
                <Route path="/medicines/add" element={<AddMedicine />} />
                <Route path="/pharmacist/add" element={<div className="page-placeholder"><h2>Add Pharmacist</h2><p>Add new pharmacist user</p></div>} />
                <Route path="/pharmacist/manage" element={<div className="page-placeholder"><h2>Manage Pharmacist</h2><p>Manage pharmacist accounts</p></div>} />
                <Route path="/reports/stock" element={<div className="page-placeholder"><h2>Stock Reports</h2><p>View inventory and stock reports</p></div>} />
                <Route path="/reports/pharmacist" element={<div className="page-placeholder"><h2>Pharmacist Reports</h2><p>View pharmacist performance reports</p></div>} />
                <Route path="/reports/sales" element={<div className="page-placeholder"><h2>Sales Reports</h2><p>View sales analytics and reports</p></div>} />
                <Route path="/invoice" element={<div className="page-placeholder"><h2>Invoice Management</h2><p>Create and manage invoices</p></div>} />
              </>
            )}

            {/* Pharmacist-only routes */}
            {userRole === 'pharmacist' && (
              <Route path="/pharmacist-dashboard" element={<PharmacistDashboard />} />
            )}

            {/* Common routes for both roles */}
            <Route path="/medicines" element={<MedicinesList userRole={userRole} />} />
            <Route path="/inventory" element={<InventoryList userRole={userRole} />} />
            <Route path="/sales" element={<SalesList userRole={userRole} />} />

            {/* Redirect unauthorized access */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
