import React, { useState, useEffect } from 'react';
import { salesAPI } from '../services/api';

const InvoiceSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('customer');
  const [sales, setSales] = useState([]);
  const [filteredSales, setFilteredSales] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchSales();
  }, []);

  useEffect(() => {
    filterSales();
  }, [searchTerm, searchType, sales]);

  const fetchSales = async () => {
    try {
      const response = await salesAPI.getAll();
      setSales(response.data);
      setFilteredSales(response.data);
    } catch (error) {
      console.error('Error fetching sales:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterSales = () => {
    if (!searchTerm) {
      setFilteredSales(sales);
      return;
    }

    const filtered = sales.filter(sale => {
      switch (searchType) {
        case 'customer':
          return sale.customer_name.toLowerCase().includes(searchTerm.toLowerCase());
        case 'id':
          return sale.id.toString().includes(searchTerm);
        case 'date':
          return sale.date.includes(searchTerm);
        default:
          return true;
      }
    });
    setFilteredSales(filtered);
  };

  const downloadInvoice = async (saleId) => {
    try {
      const response = await salesAPI.getInvoice(saleId);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `invoice-${saleId}.html`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error downloading invoice:', error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="invoice-search">
      <div className="page-header">
        <h2>Invoice Search</h2>
      </div>

      <div className="search-controls">
        <div className="search-group">
          <select 
            value={searchType} 
            onChange={(e) => setSearchType(e.target.value)}
            className="search-select"
          >
            <option value="customer">Customer Name</option>
            <option value="id">Invoice ID</option>
            <option value="date">Date</option>
          </select>

          <input
            type="text"
            placeholder={`Search by ${searchType}...`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />

          <button 
            onClick={() => setSearchTerm('')}
            className="btn btn-secondary"
          >
            Clear
          </button>
        </div>
      </div>

      <div className="search-results">
        <p>{filteredSales.length} invoice(s) found</p>

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Invoice ID</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Total Amount</th>
                <th>Final Amount</th>
                <th>Payment Method</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredSales.map(sale => (
                <tr key={sale.id}>
                  <td>#{sale.id}</td>
                  <td>{sale.customer_name}</td>
                  <td>{new Date(sale.date).toLocaleDateString()}</td>
                  <td>${sale.total_amount}</td>
                  <td>${sale.final_amount}</td>
                  <td>{sale.payment_method}</td>
                  <td>
                    <button 
                      onClick={() => downloadInvoice(sale.id)}
                      className="btn btn-sm btn-primary"
                    >
                      Download
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default InvoiceSearch;
